int main()
{
    int number_of_rounds;
    scanf("%d", &number_of_rounds);
    char *str_bob = NULL;
    char *str_alice = NULL;
    int count_a = 0;
    int count_b = 0;
    for (int i = 0; i < number_of_rounds; i++) {
        char alice_sequence[4];
        char bob_sequence[4];
        char round_tosses[21];
        scanf("%s%s%s", alice_sequence, bob_sequence, round_tosses);
        str_alice = strstr(round_tosses, alice_sequence);
        str_bob = strstr(round_tosses, bob_sequence);
        if (str_alice == str_bob){
            continue;
        }
        if (str_alice == NULL && str_bob != NULL){
            count_b++;
            continue;
        }

        if (str_alice != NULL && str_bob == NULL){
            count_a++;
            continue;
        }
        if (strlen(str_alice) < strlen(str_bob)){
            count_b++;

            continue;
        }
        if (strlen(str_alice) > strlen(str_bob)){
            count_a++;
            continue;
        }
    }
    if (count_a == count_b)
        printf("Draw!\n");

    if (count_a > count_b)
        printf("Alice wins!\n");
    if (count_a < count_b)
        printf("Bob wins!\n");

    return 0;
}

