for i in range(number_of_rounds):
    alice_sequence, bob_sequence, round_tosses = input().split()
    index_alice = round_tosses.find(alice_sequence)
    index_bob = round_tosses.find(bob_sequence)

    if index_bob == index_alice:
        bob_score +=1
        alice_score +=1
    elif index_alice == -1 or index_alice > index_bob >= 0:
        bob_score +=1
    elif index_bob == -1 or index_bob > index_alice >= 0:
        alice_score +=1

if bob_score > alice_score:
