I still hate it.  Exploding on "\\n" and such isn't obvious.
<?php
$s=explode("\\n",rtrim(fgets(STDIN)."\n"));
fscanf(STDIN,"%d",$changeCount);
$rc=[];
for($i=0;$i<$changeCount;$i++) $rc[]=explode('|',rtrim(fgets(STDIN),"\n"));
usort($rc,function ($a,$b) { if ($a[0]==$b[0]) return $a[1]<$b[1]; else return $a[0]<$b[0];} );
foreach($rc as $rv) $s[intval($rv[0])]=substr_replace($s[intval($rv[0])],$rv[2],intval($rv[1]),0);
$res=[];
foreach($s as $sv) foreach(explode("\\n",$sv) as $es) $res[]=$es;
echo(join("\n",$res)."\n");
?>
