        string s = Console.ReadLine();
        var words = Console.ReadLine().Split();
        var indexes = new HashSet<int>();
        foreach (var w in words)
        {
            var start = 0;
            var at = 0;
            while (start < s.Length && at != -1)
            {
                at = s.IndexOf(w, start);
                if (at == -1) continue;
                indexes.UnionWith(Enumerable.Range(at, w.Length));
                start = at + 1;
            }
        }
        var res = string.Concat(s.Select((c, i) => indexes.Contains(i) ? '*' : c));
        Console.WriteLine(res);
