#include <iostream>
#include <string>

using namespace std;

int main()
{
    int rows, column;
    int nbrTableServed;
    cin >> rows; cin.ignore();
    for (int i = 0; i < rows; i++)
    {
        string row;
        getline(cin, row);
        for(char c:row)
        {
            column ++;
            if(c == 'H')
            {
                cout << "Table " << i << column-1 << ", served!" << endl;
                nbrTableServed ++;
            }
        }
        column = 0;
    }

    if(nbrTableServed > 0)
    {
        cout << "No more hungry soldiers, Chief!!!" << endl;
    }
    else cout << "All tables clear, Chief!!!" << endl;
}
