def solve(pattern, values):
    global memo
    if not values:
        return 0 if '#' in pattern else 1  # Remaining must not be snake.
    while pattern and pattern[-1] == '.':
        pattern.pop()
    key = (len(pattern), len(values))
    if key in memo:
        return memo[key]
    if len(pattern) < sum(values) + len(values) - 1:
        memo[key] = 0
        return 0  # Not enough characters to make the needed snakes.
    last = values[-1]
    if not(set(pattern[-last:]) <= {'#', '?'}):
        assert '.' in pattern[-last:]
        # Can't fit the full snake, so truncate to the next dot to try again.
        while pattern[-1] != '.':
            if pattern[-1] == '#':
                memo[key] = 0
                return 0  # This needs to be the snake yet it can't be.
            check = pattern.pop()
            assert check == '?'
        return solve(pattern, values)
    if len(pattern) == last:
        solutions = 1
    elif pattern[-last-1] == '#':
        solutions = 0  # Snake at the end would be too long.
    else:
        solutions = solve(pattern[:-last-1], values[:-1])
    if pattern[-1] == '?':
        solutions += solve(pattern[:-1], values)  # If snake further in.
    memo[key] = solutions
    return solutions

for _ in range(int(input())):
    memo = {}
    pattern, values = input().split()
    print(solve(list(pattern), [int(each) for each in values.split(',')]))
