import math

def average(values):
    values = list(values)
    return sum(values) / len(values)

def quartile(values, percent):
    values = sorted(values)
    middle = (len(values) - 1) * percent
    lower = int(math.trunc(middle))
    higher = int(math.ceil(middle))
    if lower == higher:
        return values[lower]
    return values[lower] * (higher - middle) + values[higher] * (middle - lower)

def convolution(values, shift):
    shifted = values[shift :] + values[: shift]
    return sum(a * b for a, b in zip(values, shifted))

GRID_SIZE = 20
cells = [input().split() for _ in range(GRID_SIZE)]
elimination = True
while elimination:
    elimination = False
    for x, x1 in [(0, 1), (GRID_SIZE - 2, GRID_SIZE - 1)]:
        for y, y1 in [(0, 1), (GRID_SIZE - 2, GRID_SIZE - 1)]:
            if cells[y][x] == '#' and cells[y][x1] != '#' and cells[y1][x] != '#':
                cells[y][x] = 'x'
                elimination = True
    for x in range(1, GRID_SIZE - 1):
        for y in range(GRID_SIZE):
            if cells[y][x] == '#' and cells[y][x - 1] != '#' and cells[y][x + 1] != '#':
                cells[y][x] = 'x'
                elimination = True
    for x in range(GRID_SIZE):
        for y in range(1, GRID_SIZE - 1):
            if cells[y][x] == '#' and cells[y - 1][x] != '#' and cells[y + 1][x] != '#':
                cells[y][x] = 'x'
                elimintion = True

midx = average(x for row in cells for x, val in enumerate(row) if val == '#')
midy = average(y for y, row in enumerate(cells) for val in row if val == '#')

SECTORS = 24  # must be a multiple of symmetry values below
distrib = {sector: [] for sector in range(SECTORS)}
for y, row in enumerate(cells):
    for x, val in enumerate(row):
        if cells[y][x] != '#':
            continue
        coords = midx - x, midy - y
        sector = int(round(SECTORS * math.atan2(*coords) / math.tau))
        if sector < 0:
            sector += SECTORS
        distrib[sector].append(math.hypot(*coords))
perimeter = [average(distrib[sector]) for sector in range(SECTORS) if distrib[sector]]
offset = average(perimeter)
signal = [each - offset for each in perimeter]

symmetry = {n: convolution(signal, SECTORS // n) for n in [3, 4, 6, 8]}
triangle = symmetry[3] - symmetry[6]
square = symmetry[4] - symmetry[8] - symmetry[3] - symmetry[6]
if triangle > 3.0:
    print('Triangle')
elif square > 3.0 and symmetry[8] < 0.0 and not (symmetry[3] > 0.0 and symmetry[6] > 0.0):
    print('Square')
else:
    print('Circle')
