Slightly modified from code I'd written before. The main difference is that it treats each individual cycle as a different permutation during parsing.

class Permutation:

    def __init__(self, map=None):
        self.map = {} if map is None else map

    def __mul__(self, other):
        map = {}
        for start in set(self.map) | set(other.map):
            value = start
            if value in other.map:
                value = other.map[value]
            if value in self.map:
                value = self.map[value]
            if value != start:
                assert value not in map.values()
                map[start] = value
        return Permutation(map)

    def __str__(self):
        if not self.map:
            return '()'
        result = ''
        incomplete = set(self.map)
        while incomplete:
            numbers = []
            value = start = min(incomplete)
            while True:
                incomplete.remove(value)
                numbers.append(value)
                value = self.map[value]
                if value == start:
                    break
            result += '(' + ' '.join(str(each) for each in numbers) + ')'
        return result

string = input()
assert string[0] + string[-1] == '()'
answer = Permutation()
for cycle in string[1 : -1].split(')('):
    map = {}
    if numbers := cycle.split():
        for start, end in zip(numbers, numbers[1 : ] + [numbers[0]]):
            assert start not in map
            map[start] = end
    answer *= Permutation(map)
print(answer)
