    {
        if ((sentence[i] == 'u' || sentence[i] == 'U') && sentence[i] != '\0'  && sentence[i + 1] == 'b')
        {
            i += 2;
            if (sentence[i - 2] == 'U' && sentence[i] >= 97 && sentence[i] <= 122)
            {
                printf("%c", sentence[i] - 32);
            }
            else
                printf("%c", sentence[i]);
        }
        else
            printf("%c", sentence[i]);
        i++;
    }
    printf("\n");
