Comment: import scala.io.StdIn._

object Solution extends App {
  val n = readLine().toInt
  val g = readLine().toInt

  // Lecture des g grilles
  val grids: Array[Array[Array[Char]]] = Array.fill(g, n)(Array.fill(n)('.'))
  for (gridIndex <- 0 until g) {
    for (row <- 0 until n) {
      grids(gridIndex)(row) = readLine().toCharArray
    }
  }

  // Directions diagonales (4 directions)
  val directions = List((-1, -1), (-1, 1), (1, -1), (1, 1))

  def simulate(grid: Array[Array[Char]]): Array[Array[Char]] = {
    var currentGrid = grid.map(_.clone)
    var changed = true

    while (changed) {
      changed = false
      // On prépare une copie pour appliquer les infections de ce tour
      val nextGrid = currentGrid.map(_.clone)

      for {
        r <- 0 until n
        c <- 0 until n
        if currentGrid(r)(c) == 'C'
      } {
        // Pour chaque contagieux, on regarde les 4 diagonales
        for ((dr, dc) <- directions) {
          var rr = r + dr
          var cc = c + dc
          // Infection s'arrête si on rencontre un H ou hors grille
          if (rr >= 0 && rr < n && cc >= 0 && cc < n) {
            currentGrid(rr)(cc) match {
              case '.' =>
                // Infecter la personne normale
                nextGrid(rr)(cc) = 'C'
                changed = true
              case 'H' =>
                // Bloqué, ne rien faire
              case _ =>
                // Rien à faire pour C ou autre
            }
          }
        }
      }
      currentGrid = nextGrid
    }
    currentGrid
  }

  // Simuler chaque grille et compter les infectés
  val results = grids.zipWithIndex.map { case (grid, idx) =>
    val finalGrid = simulate(grid)
    val infectedCount = finalGrid.flatten.count(_ == 'C')
    (idx, infectedCount, finalGrid)
  }

  // Trouver la grille avec le plus d'infectés, en cas d'égalité prendre la plus petite index
  val (bestIdx, _, bestGrid) = results.maxBy { case (idx, infectedCount, _) => (infectedCount, -idx) }

  // Affichage
  println(bestIdx)
  bestGrid.foreach(row => println(row.mkString))
}
