    return dx*dx + dy*dy;
}

int angle(int x1, int y1, int x2, int y2, int x3, int y3)
{
    int ux = x2 - x1;
    int uy = y2 - y1;
    int vx = x3 - x1;
    int vy = y3 - y1;
    int dot = ux*vx + uy*vy;
    double lu = sqrt(ux*ux + uy*uy);
    double lv = sqrt(vx*vx + vy*vy);
    double cosa = dot/(lu*lv);
    double a = acos(cosa);
    a = a * 180 / acos(-1);
    return int(round(a));
}

