class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        int count = 0;
        if (in.hasNextLine()) {
            in.nextLine();
        }
        for (int i = 0; i < n; i++) {
            String instrument = in.nextLine();
            if (instrument.equals("guitar")) {
                String guitarNoise = count % 2 == 0 ? "plinx" : "plonx";
                System.out.println(guitarNoise);
                count++;
            } else {
                String drumNoise = i == n - 1 ? "tss" : "badum";
                System.out.println(drumNoise);
            }
        }
    }
}
