using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;
using System.Numerics;

class Solution
{
    static void Main(string[] args)
    {
        int k = int.Parse(Console.ReadLine());
        // k=1000;
        string ks = new String('1',k);
        BigInteger x = BigInteger.Parse(ks);
        var list = GetFactors(x);

        foreach (BigInteger bi in list)
        {
            Console.Error.WriteLine(bi);
        }
        Console.WriteLine(list[0]);
    }


    static IList<BigInteger> GetFactors(BigInteger n)
    {
        //edited because I'm only ever going to want the first (aka smallest) one
        List<BigInteger> factors = new List<BigInteger>();
        BigInteger x = 2;
        while (x <= n)
        {
            if (n % x == 0)
            {
                factors.Add(x);
                return factors;
                n = n / x;
            }
            else
            {
                x++;
                if (x * x >= n)
                {
                    factors.Add(n);
                    // break;
                    return factors;
                }
            }
        }
        return factors;
    }
}

