Comment: class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        String values = in.nextLine();
        String text = in.nextLine();
        String[] tab = {"left","right"};
        String[] nombre = values.split(" ");
        int rep =0;
        int resultat = 0, left = Integer.valueOf(nombre[0]),rigth = Integer.valueOf(nombre[1]);
        NumberFormat formatter =  new DecimalFormat("00");
        for(int i=0; i<tab.length;i++){
            if(tab[i].contains(text)){
                 rep = i;
            }
        }
        resultat = (rep==0)? left+(left-rigth):rigth+(rigth-left);
        resultat += (resultat<0)? 100:0;
        resultat += (resultat > 99)?-100:0;
        System.out.println(formatter.format(resultat));
    }
}
