import 'dart:io';
void main() {
    List inputs;
    inputs = stdin.readLineSync().split(' ');
    String out = "";
    for (int i = 0; i < inputs.length-1; i++) {
        out += inputs[i] + "." * int.parse(inputs[i]);
    }
    out += inputs[inputs.length-1];
    print(out);
}
