#include <bits/stdc++.h>
using namespace std;

char grid[99][99];
int main()
{
    string wife, husband; cin >> wife >> husband;
    int w_len=wife.length(), h_len=husband.length(), l=w_len*h_len/gcd(w_len, h_len);

    for (int i=0; i<l; ++i) {
        grid[0][i] = grid[i+1][l-1] = wife[i%w_len];
        grid[l+1][i] = grid[i+1][0] = husband[i%h_len];
    }
    for (int y=1; y<l+1; ++y)
        for (int x=1;x<l-1; ++x)
            grid[y][x] = ' ';

    for(int i=0; i<l+2; ++i)
        cout << grid[i] << endl;
}
