#include <iostream>
#include <string>
#include <vector>

using namespace std;

int main()
{
    int height, width;
    cin >> height >> width; cin.ignore();
    vector<vector<int>> numbers(height, vector<int>());
    for (auto &v : numbers) {
        for (int j = 0; j < width; j++) {
            int num; cin >> num; cin.ignore();
            v.push_back(num);
        }
    }
    bool first{true}, pos{true};
    for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
            char C; cin >> C; cin.ignore();
            if (C=='0') ; // skip
            else if (first) {
                pos = numbers[i][j] > 0;
                first=false;
            } else {
                bool now = numbers[i][j] > 0;
                if (now==pos) {
                    cout << "false\n";
                    exit(0);
                }
                pos = now;
            }
        }
    }

    cout << "true" << endl;
}
