k="ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
v="12010000000000111100000000110110100000001110000000001000101021";
countHoles = (S) => S.reduce((s,x)=>+v[k.indexOf(x)]+s,0);
holesInput = countHoles([...readline()]);
holesHex = countHoles([...holesInput.toString(16)]);
print(holesInput+holesHex);
