# Python
p = {"-":1, "\\":2, "/": 2, "|":3}
lines = int(input())
perimeter = 0
for i in range(lines):
    part = input()
    if i == 0 or i == lines-1:
        perimeter += sum([p[c] for c in part])
    else:
        perimeter += sum([p[c] for c in [part[0],part[-1]]])
print(perimeter)
