using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        char[] vowels = new char[]{'a','e','i','o','u'};

        int wordsAmount = int.Parse(Console.ReadLine());
        string[] words = new string[wordsAmount];
        for (int i = 0; i < wordsAmount; i++)
        {
            string word = Console.ReadLine();
            words[i] = word;
        }
        int commandsAmount = int.Parse(Console.ReadLine());
        string[,] cmd = new string[commandsAmount,2];
        for (int i = 0; i < commandsAmount; i++)
        {
            string[] inputs = Console.ReadLine().Split(' ');
            string command = inputs[0];
            string check = inputs[1];
            cmd[i,0] = command; cmd[i,1] = check;
        }

        bool nothingToPrint = true;
        for(int i =0; i<wordsAmount; i++){
            bool ok= true;
            for(int j =0; j<commandsAmount; j++){                
                switch (cmd[j,0])
                {
                    case "start":
                        if(!words[i].StartsWith(cmd[j,1])) ok = false;
                        break;
                    case "end":
                        if(!words[i].EndsWith(cmd[j,1])) ok = false;
                        break;                    
                    case "vowels":
                        if(words[i].Where(r => vowels.Contains(r)).Count() != int.Parse(cmd[j,1])) ok = false;
                        break;                        
                    case "consonants":
                        if(words[i].Where(r => !vowels.Contains(r)).Count() != int.Parse(cmd[j,1])) ok = false;
                        break;
                }
            }
            if(ok)
            {
                nothingToPrint = false;
                Console.WriteLine(words[i]);
            }
        }
        if(nothingToPrint) Console.WriteLine("NONE");
    }
}

