My JavaScript Solution:
const wordsAmount = parseInt(readline());
let words = [];
for (let i = 0; i < wordsAmount; i++) {
    words.push(readline());
}
const commandsAmount = parseInt(readline());
for (let i = 0; i < commandsAmount; i++) {
    var inputs = readline().split(' ');
    const command = inputs[0];
    const check = inputs[1];
    words = words.filter(x => {
        if(command == "start"){
            return x[0] == check;
        } else if(command == "end"){
            return x[x.length-1] == check;
        } else if(command == "vowels"){
            return (x.match(/[aeiou]/gi)||[]).length == check;
        } else {
           return (x.match(/[^aeiou]/gi)||[]).length == check;
        }
    })
}
print(words.join("\n")||"NONE");
