Comment: const N = parseInt(readline());
q = si = sj = 0; m=[];
for (let i = 0; i < N; i++) {
    let l = readline().split("");
    m.push(l);
    for(let j = 0; j < l.length; j++){
        if(l[j]=="X"){
            si += i;
            sj += j;
            q++;
        } else if(l[j]=="B"){
            bi = i;
            bj = j;
        }
    }
}
q++;
ni = bi * q - si;
nj = bj * q - sj;
if(m[ni][nj] == ".")
    m[ni][nj] = "X";
for(let i = 0; i < m.length; i++)
    print(m[i].join(""));
