[N, M] = readline().split(" ");
for (i = 0; i < M; i++) {
    x = readline();
    if ((j = x.indexOf("A")) >= 0) {
        ia = i;
        ja = j;
    }
    if ((j = x.indexOf("B")) >= 0) {
        ib = i;
        jb = j;
    }
}
print(Math.round(((ia - ib) ** 2 + (ja - jb) ** 2) ** 0.5));
