My JavaScript Solution:

var inputs = readline().split(' ');
const l = parseInt(inputs[0]);
const n = parseInt(inputs[1]);
let map = [];
for (let i = 0; i < l; i++) {
    g = [...readline().matchAll(/(..)(..)\s?\s?/g)];
    map[i] = g.map(x=>(x[2]!="  ")-(x[1]!="  "));
}
o = [];
for(j = 0; j < n; j++){
    o[j] = 0;
    for(i = 0; i < l; i++){
        o[j] += map[i][j];
    }
}
sum = o.reduce((s,x)=>s+x,0);
product = o.reduce((p,x)=>p*x);
print(sum);
print(product == 0 ? 0 : product); // damn -0
