            Console.Write("Enter integer number to decompose: ");
            if(int.TryParse(Console.ReadLine(), out int number))
            {
                int maxBits = (int)Math.Log((double)number, 2);
                for(int i = maxBits+1; i >= 0; i--)
                {
                    int cup = (int)Math.Pow(2, i);
                    if ((number & cup) != 0)
                    {
                        Console.Write("{0} ", cup);
                    }
                }
            }
            else
            {
                Console.WriteLine("Wrong format of integer");
