import java.util.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int timeout = in.nextInt();
        int nEntries = in.nextInt();

        // array 'candidates' counts the votes of each candidate
        int[] candidates = { 0, 0 };

        // map 'votes' saves the last time each user voted
        Map<Integer, Integer> votes = new HashMap<>();
        for (int i = 0; i < nEntries; i++) {
            int userId = in.nextInt();
            int vote = in.nextInt();
            int time = in.nextInt();

            // If user has never voted or enough time has passed, they can vote
            if (!votes.containsKey(userId) || time - votes.get(userId) >= timeout)
            {
                candidates[vote]++;
                votes.put(userId, time);
            }
        }
        System.out.println(candidates[0] + " " + candidates[1]);
    }
}

