n = gets.to_i
a = (1..n).map{gets.chomp.chars.select.with_index{|c,i|i%2<1}}
w = a[0].size
sweep = ->(x,y){l = [*-1..1].product([*-1..1]).map{|i,j|[x+i,y+j]} - [x,y]
    l.reject!{|i,j|[i,j].min < 0 || i > w -1 || j > n - 1}
    l.count{|i,j| a[j][i] == "*" }}
puts (0...n).map{|j|(0...w).map{|i|a[j][i] == "*"? "*" : sweep[i,j]}*" "}

