My JavaScript Solution:

const line = readline().match(/<\/?(html|body|head|div)>/g) || [];
let balanced = true;
let stack = [];
let num = 0;
line.forEach((x) => {
    if (!x.includes("/")) {
        stack.push(x);
    } else if (stack.pop() != x.replace("/","")) {
        balanced = false;
    } else {
        num++;
    }
});
if (stack.length > 0) {
    balanced = false;
}

console.log(balanced ? num : "Broken");
