total = 0;
const h = parseInt(readline());
for (let i = 0; i < h; i++) {
    var inputs = readline().split(' ');
    const w = parseInt(inputs[0]);
    const t = parseInt(inputs[1]);
    const f = inputs[2];
    const d = inputs[3];
    total += w >= 80 ? 1 : 2;
    total += f == "chocolate_chip_cookies" && d == "hot_milk" ? 1 : 0;
    total += t;
}
console.log(total);
