const distance = parseFloat(readline());
const n = parseInt(readline());
let x = [];
for (let i = 0; i < n; i++) {
    var inputs = readline().split(' ');
    const name = inputs[0];
    const speed = parseFloat(inputs[1]);
    x[i] = [name, speed];
}
x.sort((a,b)=>b[1]-a[1]);
speed1st = x[0][1];
idx2nd = x.findIndex(a=>a[1]<speed1st);
speed2nd = x[idx2nd][1];
leading = (3600*distance/speed2nd-3600*distance/speed1st).toFixed(2);
console.log(x.slice(0,idx2nd).map(a=>a[0]).join(" "),leading);
