d = {}
for i in range(int(input())):
    row = input()
    s = ''.join(sorted(list(row)))

    if s not in d:
        d[s] = [row]
    else:
        d[s] += [row]

ans = []
for k in d:
    if len(d[k]) > 1: ans += [' '.join(sorted(d[k]))]

print(*sorted(ans), sep='\n')
