import math

w,h,t1,t2,t3 = map(int, input().split())
d = {}
g1 = []
g2 = []

for i in range(h):
    s1,s2 = input().split()
    g1 += [s1]
    g2 += [s2]

    for j,c in enumerate(s1):
        if c!='.':
            d[c] = [(i,j)]

for i in range(h):
    for j in range(w):
        if g2[i][j]!='.':
            d[g2[i][j]] += [(i,j)]

ans = [['.']*w for _ in range(h)]

dt1 = t2-t1
dt2 = t3-t2

for k in sorted(d.keys()):
    p1 = d[k][0]
    p2 = d[k][1]

    x = math.floor(p2[1] + dt2 * (p2[1]-p1[1]) / dt1)
    y = math.floor(p2[0] + dt2 * (p2[0]-p1[0]) / dt1)

    if x >= w or y >= h or x < 0 or y < 0:
        continue

    if ans[y][x]=='.':
        ans[y][x] = k
    elif k < ans[y][x]:
        ans[y][x] = k

for i in range(h): print(''.join(ans[i]))
