let ip = readline();
let blocks = ip.split(":");
for(let i = 0; i < blocks.length; i++) {
    blocks[i] = removeLeadingZeros(blocks[i]);
}
ip = blocks.join(":");
ip = addDoubleColon(ip);
console.log(ip);

function removeLeadingZeros(str) {
    for(let i = 3; i > 0; i--) {
        if(str.startsWith("0".repeat(i))) {
            return str.replace("0".repeat(i), "");
        }
    }
    return str;
}

function addDoubleColon(str) {
    for(let i = 15; i > 1; i--) {
        if(str.includes("0:".repeat(i))) {
            return str.replace("0:".repeat(i), "::").replace(":::", "::");
        }
    }
    return str;
}
