"""
s = input()
d = {}

for i in range(int(input())):
    child, mother = input().split()
    if mother in d:
        d[mother] += [child]
    else:
        d[mother] = [child]

def dfs(mother, descendants=[]):
    if mother in d:
        descendants += d[mother]
        for e in d[mother]:
            dfs(e,descendants)
    return descendants

k = sorted(dfs(s))
print(*k if k else ['Nobody'], sep='\n')

