def neighbors(x, y):
    for a in (-1, 0, 1):
        for b in (-1, 0, 1):
            if a or b:
                u, v = x + a, y + b
                if 0 <= u < h and 0 <= v < w:
                    yield u, v
for x in range(h):
    row = ''
    for y in range(w):
        if grid[x][y]:
            row += '.'
        else:
            count = sum(grid[u][v] for u, v in neighbors(x, y))
            row += str(count) if count else '.'
    print(row)
