answer, best = '#', 0
grid, n = [], int(input())
for _ in range(n):
    grid.append(input())
unchecked = {(x, y) for x in range(n) for y in range(n)}
while unchecked:
    x, y = unchecked.pop()
    owner = grid[x][y].upper()
    if owner == '#':
        continue
    length, consider = 0, {(x, y)}
    while consider:
        x, y = consider.pop()
        if grid[x][y].islower():
            length += 1
        for u, v in [(x - 1, y), (x + 1, y), (x, y - 1), (x, y + 1)]:
            if (u, v) in unchecked and grid[u][v].upper() == owner:
                    unchecked.remove((u, v))
                    consider.add((u, v))
    if length > best:
        answer, best = owner, length
if best >= 5:
    print(answer, best)
else:
    print(0)

def longest(x, y, taken=None):
    global player
    if taken is None:
        taken = set()
    elif (x, y) in taken:
        return 0
    if x < 0 or y < 0 or x == len(grid) or y == len(grid[x]):
        return 0
    if grid[x][y].lower() != player:
        return 0
    taken.add((x, y))
    adjacent = (x - 1, y), (x + 1, y), (x, y - 1), (x, y + 1)
    results = [longest(*each, set(taken)) for each in adjacent]
    return max(results) + (grid[x][y] == player)

grid = []
for _ in range(int(input())):
    grid.append(input())

best, answer = 0, None
for x in range(len(grid)):
    for y in range(len(grid[x])):
        player = grid[x][y]
        if player.islower():
            length = longest(x, y)
            if length > best:
                best, answer = length, player

if best < 5:
    print(0)
else:
    print(answer.upper(), best)
