This is the code I'd recommend for the 2nd boss which simply plays the closest available move to the centre 

import java.util.Scanner;

class Player {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);

        while (true) {
            // discard opponent move
            in.nextInt(); in.nextInt();

            int validActionCount = in.nextInt();
            int row = -1;
            int col = -1;
            int center = 20;
            for (int i = 0; i < validActionCount; i++) {
                int r = in.nextInt();
                int c = in.nextInt();
                if (Math.abs(r-4) + Math.abs(c-4) < center) {
                    row = r;
                    col = c;
                    center = Math.abs(r-4) + Math.abs(c-4);
                }
            }
            System.out.println(row + " " + col);
        }
    }
}
