let [a, b, c] = readline().split(' ').map((i) => parseInt(i))

const residue = (side, div) => side / div % 1 == 0

if(a ** 2 + b ** 2 == c ** 2){
    let div = Math.ceil(Math.max(a, b))
    for(let i = div; i > 0; i--){
        if(residue(a, i) && residue(b, i) && residue(c, i)){
            console.log(a / i, b / i, c / i)
            break
        }
    }
} else console.log("NOT A TRIPLE")
