class Solution
{
    static void Main(string[] args)
    {
        int N = int.Parse(Console.ReadLine());
        List<FileDate> fd = new List<FileDate>(N);
        for (int i = 0; i < N; i++) fd.Add(new FileDate(Console.ReadLine()));
        for (int i = 0; i < N; i++)
        {
            string date = Console.ReadLine();
            fd[i].date = (int)(DateTime.ParseExact(date, "dd-MM-yyyy", System.Globalization.CultureInfo.InvariantCulture).ToOADate());
        }
        fd.Sort();
        foreach(FileDate FD in fd) Console.WriteLine(FD.name);
    }
}
public class FileDate: IComparable<FileDate>
{
    public readonly string name;
    public int date;
    public FileDate(string Name) => name = Name;
    public int CompareTo(FileDate other)
    {
        int d = this.date.CompareTo(other.date);
        if(d != 0) return d;
        return string.CompareOrdinal(this.name, other.name); // CompareOrdinal is Case sensitive !
    }
}
