        int n = int.Parse(Console.ReadLine());
        Dictionary<int,string> d = new Dictionary<int, string>(n);
        List<int> nextNode = new List<int>();
        for (int i = 0; i < n; i++) d.Add(i, Console.ReadLine());
        string[] curNode; string outStr = ""; int c = 0;
        while(true)
        {
            curNode = d[c].Split();
            if("leaf" == curNode[0])
            {
                outStr += string.Join(" ", curNode.Skip(1));
                if(nextNode.Count > 0)
                {
                    n = nextNode.Count -1;
                    c = nextNode[n];
                    nextNode.RemoveAt(n);
                }
                else break;
            } else {
                nextNode.Add(int.Parse(curNode[2]));
                c = int.Parse(curNode[1]);
            }
        }
        Console.WriteLine(outStr);
