Really like this one! Every Austrian knows their social security number, but this shows the true mechanics behind it in a playful way. :-)

For the curious: 
using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Globalization;
using System.Collections;
using System.Collections.Generic;

enum SSNExitCode { OK, CORRECTED, INVALID_SYNTAX, INVALID_DATE }

class Solution
{
    static bool IsSSNDateValid(string ssn) 
        => DateTime.TryParseExact(ssn.Substring(4,6), "ddMMyy", CultureInfo.InvariantCulture, DateTimeStyles.None, out _);

    static bool IsSSNCharactersValid(string ssn) 
        => Array.Exists(ssn.ToCharArray(), chr => !Char.IsDigit(chr));

    static string CorrectSSN(string ssn)
    {
        var ssnArr = ssn.Select(chr => (int)(chr - '0'))
                        .ToArray();

        int checksum = 0;
        int[] factors = new int[] {3, 7, 9, 0, 5, 8, 4, 2, 1, 6};
        for(int i = 0; i < ssnArr.Length; i++)
            checksum += ssnArr[i] * factors[i];

        if(checksum % 11 < 10)
            return ssn.Substring(0,3) + checksum % 11 + ssn.Substring(4,6);
        else
            return CorrectSSN(int.Parse(ssn.Substring(0,3))+1 + ssn.Substring(3,7));
    }

    static (SSNExitCode, string) ValidateSSN(string ssn) 
    {
        if(ssn[0] == '0' || ssn.Length != 10 || IsSSNCharactersValid(ssn))
            return (SSNExitCode.INVALID_SYNTAX, null);

        if(!IsSSNDateValid(ssn))
            return (SSNExitCode.INVALID_DATE, null);

        string corrected = CorrectSSN(ssn);
        if(corrected == ssn)
            return (SSNExitCode.OK, ssn);
        else
            return (SSNExitCode.CORRECTED, corrected);
    }

    static void Main(string[] args)
    {
        int len = int.Parse(Console.ReadLine());
        for(int i = 0; i < len; i++) 
        {
            var input = Console.ReadLine();
            var result = ValidateSSN(input);

            switch(result.Item1) 
            {
                case SSNExitCode.OK:
                Console.WriteLine("OK");
                break;
                case SSNExitCode.INVALID_SYNTAX:
                Console.WriteLine("INVALID SYNTAX");
                break;
                case SSNExitCode.INVALID_DATE:
                Console.WriteLine("INVALID DATE");  
                break;          
                case SSNExitCode.CORRECTED:
                Console.WriteLine(result.Item2);
                break;
            }
        }
    }
}
