program Answer;
{$H+}
uses sysutils, classes, math;

// Helper to read a line and split tokens
procedure ParseIn(Inputs: TStrings) ;
var Line : string;
begin
    readln(Line);
    Inputs.Clear;
    Inputs.Delimiter := ' ';
    Inputs.DelimitedText := Line;
end;

var
    a, b, n : Int32;
    Inputs: TStringList;
begin
    Inputs := TStringList.Create;
    ParseIn(Inputs);
    n := StrToInt(Inputs[0]);

    // Write an answer using writeln()
    // To debug: writeln(StdErr, 'Debug messages...');
    a := ((n-2)*180) div n;
    b := 360 div n;
    writeln(format('%d %d', [a, b]));
    if (a mod 2 = 0) and (b mod 2 = 0) then begin
    writeln('even');
    end else begin
    writeln('odd');
    end;

    flush(StdErr); flush(output); // DO NOT REMOVE
end.

