int n = int.Parse(Console.ReadLine());
int intAngle = (n -2)*180 / n;
Console.WriteLine(intAngle.ToString() + " " + (180 - intAngle).ToString());
Console.WriteLine((intAngle % 2) > 0 ? "odd" : "even");
