using System;

class Solution
{
    static void Main(string[] args)
    {
        int N = int.Parse(Console.ReadLine());
        string[] a = new string[N];
        for(int i=0; i<N;i++)
        {
            string h="";
            for(int j=1; j<=i+1;j++)
            {
                h = j+h;
            }
            a[i] = h;
        }
        for(int i=0; i<N;i++)
        {
            Console.WriteLine(a[i]);
        }
        for(int i=a.Length-2; i>=0;i--)
        {
            Console.WriteLine(a[i]);
        }
    }
}

