        string outStr = "";
        foreach(string s in Console.ReadLine().Split())
        {
            outStr += "-" + s.Substring(0, 1);
            char c = s.FirstOrDefault(c => char.IsDigit(c));
            if(char.IsDigit(c)) outStr += new string('*', c - 48);
        }
        Console.WriteLine(outStr.Substring(1));
