Bellow my C# solution :
int gridSide = int.Parse(Console.ReadLine());
string backgroundCharForegroundChar = Console.ReadLine();
string[] inputs = Console.ReadLine().Split(' ');
int pointAx = int.Parse(inputs[0]);
int pointAy = int.Parse(inputs[1]);
int pointBx = int.Parse(inputs[2]);
int pointBy = int.Parse(inputs[3]);
int pointCx = int.Parse(inputs[4]);
int pointCy = int.Parse(inputs[5]);
string[] grid = Enumerable.Range(0, gridSide).Select(i => new string(backgroundCharForegroundChar[0], gridSide)).ToArray();
List<int> X = new List<int>(){pointAx, pointBx, pointCx};
List<int> Y = new List<int>(){pointAy, pointBy, pointCy};
for(int i = 0; i < 2; i++)
{
    int x = X[i]; int y = Y[i];
    int x2 = X[1+i]; int y2 = Y[1+i];
    bool loop = true;
    while(loop)
    {
        grid[y] = grid[y].Substring(0, x) + backgroundCharForegroundChar[1] + grid[y].Substring(x+1);
        loop = x2 != x || y2 != y;
        if(x < x2) x++; else if(x > x2) x--;
        if(y < y2) y++; else if(y > y2) y--;
    }
}
for(int i = 0; i < gridSide; i++) Console.WriteLine(grid[i]);
