 import re

def to_int(x: str) -> str:
    X = x
    l = re.findall('[|]+', x)
    while l:
        m = max(l,key=len)
        X=X.replace(m,str(len(m)))
        l.remove(m)
    return X.replace('~','0')

def to_eso(x: int) -> str:
    s=''
    for i in str(x):
        if i in'.-':
            s+=i
        elif i=='0':
            s+='~'
        else:
            s+='|'*int(i)
    return s

