use std::io;

struct State {
    value1: String,
    value2: String,
    op: char,
    invec: Vec<char>,
    invec_next: usize,
    invalid: bool,
}

fn main() {
    let mut input_line = String::new();
    io::stdin().read_line(&mut input_line).unwrap();
    let s = input_line.trim_matches('\n').to_string();
    // eprintln!("{s}");
    let mut state = State {
        value1: String::new(),
        value2: String::new(),
        op: '+',
        invec: s.chars().collect::<Vec<_>>(),
        invec_next: 0,
        invalid: false,
    };
    while let Some(&c) = state.invec.get(state.invec_next) {
        match c {
            '|' | '~' => parse_num(&mut state),
            '+' | '-' | '*' | '/' => {
                state.op = c;
                state.invec_next+=1;
            }
            _ => {state.invalid = true; break},
        }
    }
    if state.invalid {
        println!("Syntax Error");
    } else {
        let v1: f32 = state.value1.parse().unwrap_or_default(); // panic if this fails.
        let v2: f32 = state.value2.parse().unwrap_or_default();
        let mut result = match state.op {
            '+' => v1 + v2,
            '*' => v1 * v2,
            '/' => v1 / v2,
            '-' => v1 - v2,
            _ => {
                state.invalid = true;
                0.0
            }
        };
        result = chop(result);
        println!("{}{}{}", state.value1, state.op, state.value2);
        //eprintln!("{}", result);
        println!("{}", to_bars(result));
    }
}

fn to_bars(num: f32) -> String {
    num.to_string()
        .chars()
        .map(|digit| match digit {
            '0' => "~".to_string(),
            '1'..='9' => "|".repeat(digit.to_string().parse::<usize>().unwrap_or(0)),
            '.' => ".".to_string(),
            '-' => "-".to_string(),
            _ => "ERROR".to_string(),
        })
        .collect()
}
fn parse_num(state: &mut State) {
    let mut num = 0;
    let mut strnum = String::new();
    while let Some(&c) = state.invec.get(state.invec_next) {
        match c {
            '|' => {
                state.invec_next+=1;
                num += 1;
            }
            '~' => {
                state.invec_next+=1;
                if num > 0 {
                    strnum += num.to_string().as_str();
                }
                num = 0;
                strnum += "0";
            }
            '.' => {
                state.invec_next+=1;
                if num > 0 {
                    strnum += num.to_string().as_str();
                }
                num = 0;
                strnum += ".";
            }
            _ => break,
        }
    }
    if num > 0 {
        strnum += num.to_string().as_str();
    }
    if state.value1=="" {
        state.value1 = strnum;
    } else {
        state.value2 = strnum;
    }
}

fn chop(num: f32) -> f32 {
    let least = 10.0f32.powf(5.0);
    (num * least).round() / least
}
