        int n = int.Parse(Console.ReadLine());
        int[] size = null;
        List<string[]> lines = new List<string[]>(n);
        for (int i = 0; i < n; i++)
        {
            string[] line = Console.ReadLine().Split();
            lines.Add(line);
            if(null == size) size = new int[line.Count()];
            for(int j = 0 ; j < line.Count(); j++) if(size[j] < line[j].Length) size[j] = line[j].Length;
        }
        for (int i = 0; i < n; i++)
        {
            string[] curLine = lines[i];
            string strOut = "";
            for(int j = 0 ; j < size.Count(); j++) strOut += " " + curLine[j] + new string(' ', size[j] - curLine[j].Length);
            Console.WriteLine(strOut.Trim());
        }
