program Answer;
{$H+}
uses sysutils, classes, math;

// Helper to read a line and split tokens
procedure ParseIn(Inputs: TStrings) ;
var Line : string;
begin
    readln(Line);
    Inputs.Clear;
    Inputs.Delimiter := ' ';
    Inputs.DelimitedText := Line;
end;
function remove0(value:String):String;
begin
remove0 := value;
while (length(remove0)> 1) and (remove0[1] = '0') do begin
delete(remove0,1,1);
end;
end;
var
    N : Int32;
    elementListe : Int32;
    i : Int32;
    Inputs: TStringList;
    odds, evens:TStringlist;
    t:String;
begin
    Inputs := TStringList.Create;
    ParseIn(Inputs);
    N := StrToInt(Inputs[0]);
    odds:=TStringlist.Create;
    odds.sorted := true;
    evens:=TStringlist.Create;
    evens.sorted := true;
    for i := 0 to N-1 do
    begin
        ParseIn(Inputs);
        elementListe := StrToInt(Inputs[0]);
        if elementListe mod 2 = 0 then begin
          evens.add(format('%0.3d', [elementListe]));
        end else begin
          odds.add(format('%0.3d', [elementListe]));         
        end;
    end;
    t := '';
    for i := 0 to evens.count -1 do begin
      t := t + remove0(evens[i])+ ' ';
    end;
    for i := 0 to odds.count -1 do begin
      t := t + remove0(odds[i])+ ' ';
    end;

    // Write an answer using writeln()
    // To debug: writeln(StdErr, 'Debug messages...');

    writeln(trim(t));
    flush(StdErr); flush(output); // DO NOT REMOVE
end.
